'use strict';
var gulp = require("gulp");
var webpack = require("webpack-stream");

gulp.task('default', function () {
    gulp.src('./index.js')
	    .pipe(webpack({
		    output: {
			    filename: 'login.js',
			    pathinfo: true
		    },
		    module: {
			    pathinfo: true
		    }
	    }))
	    .pipe(gulp.dest('./test/'));
});